#DONT CHANGE THIS FUNCTION
def part1(samples):
    #extract features
    X = extract_features(samples)
    assert type(X) == np.ndarray
    print("Example sample feature vec: ", X[0])
    print("Data shape: ", X.shape)
    return X


def extract_features(samples):
    print("Extracting features ...")
    dicofwords, wordcounts  = read_text(samples)
    wordstoremove = {}
    for k in sorted(wordcounts, key=wordcounts.get, reverse=False):
        if  wordcounts[k] <= 50:
            wordstoremove[k] = wordcounts[k]
    updatedDict = []
    for i in range(len(dicofwords)):
        d = {}
        d = dicofwords[i].copy()
        for k, v in dicofwords[i].items():
            if k in wordstoremove:
                d.pop(k)
        updatedDict.append(d)
    df = pd.DataFrame(updatedDict)
    res = df.to_numpy()
    res = np.nan_to_num(res)
    return res



