def shuffle_split(X,y):
    randindex = list(range(len(X)))
    random.shuffle(randindex)
    counter = 0
    train_X, train_Y, test_X, test_Y = [], [], [], []
    for i in randindex:
        counter += 1
        if counter <= 0.8 * len(X):
            train_X.append(X[i])
            train_Y.append(y[i])
        else:
            test_X.append(X[i])
            test_Y.append(y[i])
    return train_X, test_X, train_Y, test_Y


